mapper.update:
update student
set
violation_count = violation_count + 1,
violation_score = violation_score + #{score}
where id = #{id}

mapper.delete:
delete from student
where id in

    #{id}

mapper.select:
select student.id, student.name, no, gender, phone, id_card, is_college, address, degree, graduation_date, clazz_id, violation_count, violation_score, student.create_time, student.update_time, clazz.name clazzName from student left join clazz on student.clazz_id = clazz.id
student.name like concat ('%', #{name}, '%')
and degree = #{degree}
and clazz_id = #{clazzId}

select id, name, no, gender, phone, degree, id_card, is_college, address, graduation_date, violation_count, violation_score, clazz_id, create_time, update_time
from student
where id = #{id}
